function markCompleted(n){n.disabled=!0;var t={id:n.name};$.post("/Todo/MarkDone",t,function(){var t=n.parentElement.parentElement;$(t).addClass("done")})}function addItems(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t=$("#add-item-date").val(),i={title:n,dueAt:t};$.post("todo/AddItem",i,function(){window.location="/todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t).show()}})}$(document).ready(function(){$("#add-item-button").on("click",addItems);$(".done-checkbox").on("click",function(n){markCompleted(n.target)})});